    private void regbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_regbtnActionPerformed
if(!apaternotxt.getText().isEmpty()&&!amaternotxt.getText().isEmpty()&& !nombrestxt.getText().isEmpty()&&!telefonotxt.getText().isEmpty()){
            conectar con=new conectar();
            
            ResultSet resultado;
           resultado=con.buscar("select * from clientes where apaterno='"+apaternotxt.getText()+"' and amaterno='"+amaternotxt.getText()+"' and nombres='"+nombrestxt.getText()+"'");
            try {
                         if(resultado.next()){
                             JOptionPane.showMessageDialog(rootPane, "El usuario ya existe");
                        
                             }
                        else{
                      con.agregar("insert into clientes (id, apaterno, amaterno, nombres, sexo, telefono) values (null, '"+apaternotxt.getText()+"', '"+amaternotxt.getText()+"', '"+nombrestxt.getText()+"','"+sexolist.getSelectedIndex()+"', '"+telefonotxt.getText()+"')");
      JOptionPane.showMessageDialog(rootPane, "Los datos han sido guardados correctamente");
                         
                         }
           
             
            }
                          catch (SQLException ex) {
                 JOptionPane.showMessageDialog(rootPane, "Error al buscar3");

            } 
            
        }       
else{
     JOptionPane.showMessageDialog(rootPane, "Todos los campos son obligatorios");
}
    }//GEN-LAST:event_regbtnActionPerformed

