    private void nombrestxtKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_nombrestxtKeyTyped
        int k=(int)evt.getKeyChar();
    if (k == 'a' || k == 'A' || k == 'b' || k == 'B' || k == 'c' || k == 'C'|| k == 'd' || k == 'D'|| k == 'e' || k == 'E'|| k == 'f' || k == 'F'|| k == 'g' || k == 'G'|| k == 'h' || k == 'H'|| k == 'i' || k == 'I'|| k == 'j' || k == 'j'|| k == 'k' || k == 'K'|| k == 'l' || k == 'L'|| k == 'm' || k == 'M'|| k == 'n' || k == 'N'|| k == 'ñ' || k == 'Ñ'|| k == 'o' || k == 'O'|| k == 'p'|| k == 'P'|| k == 'q' || k == 'Q'|| k == 'r' || k == 'R'|| k == 's' || k == 'S'|| k == 't' || k == 'T'|| k == 'u' || k == 'U'|| k == 'v' || k == 'V'|| k == 'w' || k == 'W'|| k == 'x' || k == 'X'|| k == 'y' || k == 'Y'|| k == 'z' || k == 'Z'|| k == ' '  ){ 
        
        if(nombrestxt.getText().length()>=25){
            evt.consume();
        }
    }else{
        evt.consume();
    }
    }//GEN-LAST:event_nombrestxtKeyTyped

