    private void aceptbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_aceptbtnActionPerformed
        if(!apaternotxt.getText().isEmpty()&&!amaternotxt.getText().isEmpty()&& !nombrestxt.getText().isEmpty()){
            conectar con=new conectar();
            
            ResultSet resultado;
           resultado=con.buscar("select * from clientes where apaterno='"+apaternotxt.getText()+"' and amaterno='"+amaternotxt.getText()+"' and nombres='"+nombrestxt.getText()+"'");
            try {
                         if(resultado.next()){
                          id=resultado.getInt("id");
                          
                          
            
            
           resultado=con.buscar("select * from clientes where id='"+id +"'");
            try {
                         if(resultado.next()){
                             clientelbl.setText(resultado.getString("apaterno")+" "+resultado.getString("amaterno")+" "+resultado.getString("nombres"));
                             }
                   }
                          catch (SQLException ex) {
               }
                             }
                        else{
       JOptionPane.showMessageDialog(rootPane, "El usuaio no existe");
                         
                         }
           
             
            }
                          catch (SQLException ex) {
                 JOptionPane.showMessageDialog(rootPane, "Error al buscar3");

            } 
            
        }       
else{
     JOptionPane.showMessageDialog(rootPane, "Los campos Nombre(s), Apellido Paterno y Apellido Materno son obligatorios");
}
    }//GEN-LAST:event_aceptbtnActionPerformed

    private void busbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_busbtnActionPerformed
        if(!apaternotxt.getText().isEmpty()&&!amaternotxt.getText().isEmpty()&& !nombrestxt.getText().isEmpty()){
            conectar con=new conectar();
            
            ResultSet resultado;
           resultado=con.buscar("select * from clientes where apaterno='"+apaternotxt.getText()+"' and amaterno='"+amaternotxt.getText()+"' and nombres='"+nombrestxt.getText()+"'");
            try {
                         if(resultado.next()){
                             nombrestxt.setText(resultado.getString("nombres"));
                             apaternotxt.setText(resultado.getString("apaterno"));
                             amaternotxt.setText(resultado.getString("amaterno"));
                             sexolist.setSelectedIndex(resultado.getInt("sexo"));
                             telefonotxt.setText(resultado.getString("telefono"));
                             }
                        else{
       JOptionPane.showMessageDialog(rootPane, "El usuaio no existe");
                         
                         }
           
             
            }
                          catch (SQLException ex) {
                 JOptionPane.showMessageDialog(rootPane, "Error al buscar3");

            } 
            
        }       
else{
     JOptionPane.showMessageDialog(rootPane, "Los campos Nombre(s), Apellido Paterno y Apellido Materno son obligatorios");
}
    }//GEN-LAST:event_busbtnActionPerformed

